mapper.select:
SELECT 

FROM dl_order_tuan a

WHERE a.id = #{id}

SELECT
FROM dl_order_tuan a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.username LIKE '%'||#{username}||'%' '%'+#{username}+'%' concat('%',#{username},'%')
AND a.mobile = #{mobile}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.artno = #{artno}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM dl_order_tuan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dl_order_tuan SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
