mapper.select:
SELECT 

FROM s_goods a

WHERE a.id = #{id}

SELECT
a.supplier_id AS "supplier.id", a.name AS "name", a.artno AS "artno", a.logo AS "logo", a.market_price AS "marketPrice", a.price AS "price", sku.spec1 AS "spec1", sku.spec2 AS "spec2", sku.id as "id", sku.settlement_price AS "settlementPrice" FROM s_goods a
left join s_goods_sku sku on a.id =sku.goods_id WHERE a.artno = #{artno} and a.pass='1' and a.state='1' and (sku.spec1=#{spec1} or sku.spec2=#{spec1} ) and a.del_flag = #{DEL_FLAG_NORMAL}

SELECT 

FROM s_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE  concat('%',#{name},'%')


		AND a.sync = #{sync}


		AND a.syncby = #{syncby}


		AND a.pass = #{pass}


		AND a.artno = #{artno}


		AND a.brand_id = #{brand.id}


		AND a.supplier_id = #{supplier.id}


		AND a.create_by = #{createBy.id}



		AND a.state = #{state}


		AND a.market_price BETWEEN #{beginMarketPrice} AND #{endMarketPrice}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.price BETWEEN #{beginPrice} AND #{endPrice}




		ORDER BY ${page.orderBy}


		ORDER BY a.num DESC

SELECT 

FROM s_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_goods SET
	sync = #{sync}
WHERE id = #{id}

UPDATE s_goods SET
	syncby = #{syncby}
WHERE id = #{id}

UPDATE s_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE s_goods SET
state = #{state}
WHERE id in

	#{item}

UPDATE s_goods SET
pass = #{pass}
WHERE id in

	#{item}
