mapper.select:
SELECT 

FROM s_order_after_sales a

WHERE a.id = #{id}

SELECT 

FROM s_order_after_sales a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.ordernumber = #{ordernumber}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_order_after_sales a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_order_after_sales SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
