mapper.select:
SELECT 

FROM s_order a

WHERE a.id = #{id}

SELECT 

FROM s_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.num = #{num}


		AND a.ordernumber = #{ordernumber}


		AND a.phone = #{phone}


		AND a.consignee = #{consignee}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
