mapper.select:
SELECT 

FROM s_user a

WHERE a.id = #{id}

SELECT 

FROM s_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone = #{phone}


		AND a.nickname = #{nickname}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
